import React,{useState} from 'react'

function Form(props) {

  const [title,setTitle] = useState(props.article.title)
  const [description,setDescription] = useState(props.article.description)
  return (
    <div>
      {/* {props.article && props.article.title } */}
      {props.article ? (
        <div className='mb-3'>
        <label htmlFor='title' className='form-lable'>Title</label>
        <input type='text' className= "form-control"  id="title" placeholder='Enter Title' value={title} />

        <label htmlFor='description' className='form-lable'>Description</label>
        <textarea type='text' className= "form-control"  row="5" id="desc" placeholder='Enter description'  value={description}/>
        <br/>

        <button className='btn btn-success'>Update</button>
        </div>

      ) : null}

    </div>
  )
}

export default Form
