import './App.css';
import {useState,useEffect} from 'react'
import Article from './component/Article';
import Form from './component/Form'

function App() {

  const [articles,setArticles] = useState([])
  const [editArticle,setEditArticles] = useState([])

  useEffect(()=>{
    fetch("http://127.0.0.1:8000/api/article/",{
      'method':'GET',
      headers:{
        'Content-Type':'application/json',
        'Authorization':'Token 505c0f4221b2e4844ef0161431402e144d47d6aa'
      }
    })
    .then(resp => resp.json())
    .then(resp => setArticles(resp))
    .catch(error => console.log(error))
  },[])

  const editBtn = (article) => {
    setEditArticles(article)

  }

  return (
    <div className="App">
      <h1>Django - React Blog</h1>
      <Article articles = {articles} editBtn={editBtn}/>
      {editArticle ? <Form article={editArticle} /> :null }


    </div>
  );
}

export default App;
